import React, { useState } from 'react';
import '../css/Signup.css'; // Certifique-se de que os estilos correspondam ao seu design

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/signup', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
      } else {
        setMessage(data.message || 'Erro ao cadastrar.');
      }
    } catch (error) {
      console.error('Erro:', error);
      setMessage('Erro de conexão.');
    }
  };

  return (
    <div id="webcrumbs">
      <div className="w-full flex justify-center">
        <div className="w-[1200px] p-8 bg-gradient-to-br from-primary-50 to-primary-100 font-sans">
          <div className="bg-white rounded-xl shadow-lg overflow-hidden flex flex-col md:flex-row">
            <div className="w-full md:w-1/2 bg-primary-600 p-8 flex flex-col justify-center relative overflow-hidden">
              <div className="absolute -right-20 -top-20 w-64 h-64 bg-primary-500 rounded-full opacity-30"></div>
              <div className="absolute -left-20 -bottom-20 w-64 h-64 bg-primary-700 rounded-full opacity-30"></div>
              <h1 className="text-4xl font-bold text-white mb-4 relative z-10">Join our community</h1>
              <p className="text-primary-100 mb-6 relative z-10">Create an account to unlock all features and start your journey with us.</p>
              <div className="space-y-4 relative z-10">
                <div className="flex items-center">
                  <span className="material-symbols-outlined text-primary-200 mr-3">check_circle</span>
                  <p className="text-white">Access to exclusive content</p>
                </div>
                <div className="flex items-center">
                  <span className="material-symbols-outlined text-primary-200 mr-3">check_circle</span>
                  <p className="text-white">Save your favorites across devices</p>
                </div>
                <div className="flex items-center">
                  <span className="material-symbols-outlined text-primary-200 mr-3">check_circle</span>
                  <p className="text-white">Join our thriving community</p>
                </div>
              </div>
            </div>
            <div className="w-full md:w-1/2 p-8 md:p-12">
              <h2 className="text-2xl font-bold mb-6">Create your account</h2>
              <form onSubmit={handleSubmit} className="space-y-5">
                <div className="space-y-1">
                  <label htmlFor="username" className="text-sm font-medium">Primeiro Nome</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-all duration-200"
                    placeholder="Digit seu primeiro nome"
                    required
                  />
                </div>
                <div className="space-y-1">
                  <label htmlFor="email" className="text-sm font-medium">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-all duration-200"
                    placeholder="Digite seu email"
                    required
                  />
                </div>
                <div className="space-y-1">
                  <label htmlFor="password" className="text-sm font-medium">Senha</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-all duration-200"
                    placeholder="Create a password"
                    required
                  />
                  <p className="text-xs text-gray-500 mt-1">Must be at least 8 characters with numbers and special characters</p>
                </div>
                <div className="flex items-start space-x-2 mt-4">
                  <input
                    type="checkbox"
                    id="terms"
                    className="mt-1 h-4 w-4 text-primary-600 border-gray-300 rounded focus:ring-primary-500 cursor-pointer"
                    required
                  />
                  <label htmlFor="terms" className="text-sm">
                    I agree to the <a href="#" className="text-primary-600 hover:text-primary-700 underline transition-colors">Terms of Service</a> and <a href="#" className="text-primary-600 hover:text-primary-700 underline transition-colors">Privacy Policy</a>
                  </label>
                </div>
                <button type="submit" className="w-full bg-primary-600 hover:bg-primary-700 text-white font-medium py-3 rounded-lg transition-all duration-200 transform hover:scale-[1.01] hover:shadow-md">
                  Create Account
                </button>
                <div className="relative flex items-center justify-center mt-6">
                  <div className="border-t border-gray-300 absolute w-full"></div>
                  <div className="bg-white px-4 relative text-sm text-gray-500">or continue with</div>
                </div>
                <div className="grid grid-cols-1 gap-3 flex justify-center">
                  <button className="flex items-center justify-center py-2.5 px-4 border border-gray-300 rounded-lg hover:bg-gray-50 transition-all duration-200 w-full md:w-auto mx-auto">
                    <i className="fa-brands fa-google text-xl mr-2"></i>
                    Google
                  </button>
                </div>
              </form>
              <div className="text-center mt-6">
                <p className="text-sm">Already have an account? <a href="#" className="text-primary-600 hover:text-primary-700 font-medium hover:underline transition-all">Sign in</a></p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signup;
